package com.syntax.class07;

public class loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=1;
		int y=50;
		while(x<21) {
			System.out.println(x);
			x=x+2;
		}
		
		System.out.println("*************************");
		while(y>0) {
			System.out.println(y);
			y--;
		}
	}

}
